var plus = require('./plus.js');
var minus = require('./minus.js');
var multiple = require('./multiple.js');
var divide = require('./divide.js');

exports.plus = function(a, b) {
	console.log("plus(%d, %d) = %d", a, b, plus(a, b));
}

exports.minus = function(a, b) {
	console.log("minus(%d, %d) = %d", a, b, minus(a, b));
}

exports.multiple = function(a, b) {
	console.log("multiple(%d, %d) = %d", a, b, multiple(a, b));
}

exports.divide = function(a, b) {
	console.log("divide(%d, %d) = %d", a, b, divide(a, b));
}